mapper.select:
SELECT * FROM t_school_dual_subject
LEFT JOIN t_school_subject on t_school_dual_subject.school_dual_subject_school_subject = t_school_subject.school_subject_code
LEFT JOIN t_info_school on t_school_subject.school_subject_school = t_info_school.school_id
LEFT JOIN t_info_subject on t_school_subject.school_subject_subject = t_info_subject.subject_id
LEFT JOIN t_info_discipline on t_info_subject.subject_discipline = t_info_discipline.discipline_id

    t_school_subject.school_subject_school = #{schoolId}
    AND t_info_subject.subject_discipline = #{disciplineId}
    AND t_school_dual_subject.school_dual_subject_year = #{year}

ORDER BY t_info_school.school_id,t_info_subject.subject_name

SELECT * FROM t_school_dual_subject LEFT JOIN t_school_subject on t_school_dual_subject.school_dual_subject_school_subject = t_school_subject.school_subject_code LEFT JOIN t_info_school on t_school_subject.school_subject_school = t_info_school.school_id LEFT JOIN t_info_subject on t_school_subject.school_subject_subject = t_info_subject.subject_id LEFT JOIN t_info_discipline on t_info_subject.subject_discipline = t_info_discipline.discipline_id
t_school_subject.school_subject_school = #{schoolId} AND t_school_dual_subject.school_dual_subject_year = #{year}
ORDER BY t_info_school.school_id,t_info_subject.subject_name

SELECT DISTINCT school_dual_subject_year FROM t_school_dual_subject
LEFT JOIN t_school_subject on t_school_dual_subject.school_dual_subject_school_subject = t_school_subject.school_subject_code
WHERE t_school_subject.school_subject_school = #{schoolId}
